{
    "tiddlers": {
        "$:/plugins/ajh/favorites": {
            "core-version": ">=5.1.14",
            "created": "20150902052640307",
            "creator": "Andrew Harrison",
            "dependents": "$:/language/Buttons/Favorites/Caption $:/language/Buttons/Favorites/Hint $:/language/SideBar/Favorites/Caption $:/plugins/ajh/favorites/icon $:/plugins/ajh/favorites/readme $:/plugins/ajh/favorites/SideBar $:/plugins/ajh/favorites/image $:/plugins/ajh/favorites/stylesheet $:/plugins/ajh/favorites/template",
            "description": "Favorites",
            "list": "readme",
            "modified": "20170521202900774",
            "modifier": "Andrew Harrison",
            "plugin-type": "plugin",
            "source": "http://favorites.tiddlyspot.com",
            "title": "$:/plugins/ajh/favorites",
            "type": "application/json",
            "version": "3.0.3",
            "text": "{
    "tiddlers": {
        "$:/language/Buttons/Favorites/Caption": {
            "text": "favorite",
            "title": "$:/language/Buttons/Favorites/Caption"
        },
        "$:/language/Buttons/Favorites/Hint": {
            "text": "Add to favorites list",
            "title": "$:/language/Buttons/Favorites/Hint"
        },
        "$:/language/SideBar/Favorites/Caption": {
            "text": "Favorites",
            "title": "$:/language/SideBar/Favorites/Caption"
        },
        "$:/plugins/ajh/favorites/icon": {
            "text": "\\define my-svg()\n<svg class='tc-image-favorites tc-image-button' width='22pt' height='22pt' viewBox='0 0 192 192'><path fill='#f08' stroke='#30f' stroke-width='7.5' d='m13.5,52.2l82.5,-48l82.5,48v87.3l-82.5,48l-82.5,-48v-87.3z'/><path fill='#ff0' stroke='#f90' stroke-width='5' d='m$(m)$$(path)$'/></g></svg>\n\\end\n\n<$vars m='96,122' path={{$:/plugins/ajh/favorites/image!!path}}>\n<<my-svg>>\n</$vars>",
            "title": "$:/plugins/ajh/favorites/icon",
            "tags": "$:/tags/Image"
        },
        "$:/plugins/ajh/favorites/readme": {
            "text": "This plugin adds the <span class='tc-btn-dropdown'>{{$:/core/images/star-filled}}</span> favorites button to the View Toolbar and also the <span class='tc-btn-dropdown'>{{$:/core/images/star-filled}}</span> tab to the Sidebar. The favorites button turns yellow <span class='tc-btn-dropdown'>{{$:/plugins/ajh/favorites/image}}</span> when selected and then the tiddler appears in the list on the <span class='tc-btn-dropdown'>{{$:/core/images/star-filled}}</span> tab. The list can be sorted using drag and drop. To drag an item, select it, then hold down the mouse or trackpad button while you drag it to a new location in the list. Release the button to drop the item into place. Unneeded favorites can be removed from the list by using the <span class='tc-btn-dropdown'>{{$:/plugins/ajh/favorites/image}}</span> favorites button in the tiddler.\n\nSimply drag $:/plugins/ajh/favorites to the top of your tiddlywiki display, save and reload.\n\nThis plugin comprises:\n\n* Template\n* Sidebar tab",
            "title": "$:/plugins/ajh/favorites/readme"
        },
        "$:/plugins/ajh/favorites/SideBar": {
            "text": "\\define drop-actions()\n<$action-listops $tiddler='$:/plugins/ajh/favorites/list' $subfilter='+[insertbefore:currentTiddler<actionTiddler>]'/>\n\\end\n\n<$list filter='[list[$:/plugins/ajh/favorites/list]]'>\n<div style='position: relative;'>\n<$droppable actions=<<drop-actions>>>\n<div class='tc-droppable-placeholder'>\n&nbsp;\n</div>\n<div>\n <$link to={{!!title}}>\n<$view field='caption'><$view field='title'/></$view></$link>\n</div>\n</$droppable>\n</div>\n</$list>\n",
            "title": "$:/plugins/ajh/favorites/SideBar",
            "tags": "$:/tags/SideBar",
            "list-before": "",
            "caption": "<span class='tc-btn-dropdown' title={{$:/language/SideBar/Favorites/Caption}}>{{$:/core/images/star-filled}}</span><span class='tc-favorites-hidden'>{{$:/language/SideBar/Favorites/Caption}}</span>"
        },
        "$:/plugins/ajh/favorites/template": {
            "text": "\\define add() [[$(currentTiddler)$]]\n\\define remove() -[[$(currentTiddler)$]]\n\n<$list filter='[list[$:/plugins/ajh/favorites/list]is[current]]'><$button tooltip={{$:/language/Buttons/Favorites/Hint}} aria-label={{$:/language/Buttons/Favorites/Caption}} class=<<tv-config-toolbar-class>>>\n<$action-listops $tiddler='$:/plugins/ajh/favorites/list' $field='list' $subfilter=<<remove>>/>\n<$list filter='[<tv-config-toolbar-icons>prefix[yes]]'>\n{{$:/plugins/ajh/favorites/image}}\n</$list>\n<$list filter='[<tv-config-toolbar-text>prefix[yes]]'>\n<span class='tc-btn-text'>\n<$text text={{$:/language/Buttons/Favorites/Caption}}/>\n</span>\n</$list>\n</$button></$list><$list filter='[all[current]!list[$:/plugins/ajh/favorites/list]]'><$button tooltip={{$:/language/Buttons/Favorites/Hint}} aria-label={{$:/language/Buttons/Favorites/Caption}} class=<<tv-config-toolbar-class>>>\n<$action-listops $tiddler='$:/plugins/ajh/favorites/list' $field='list' $subfilter=<<add>>/>\n<$list filter='[<tv-config-toolbar-icons>prefix[yes]]'>\n{{$:/core/images/star-filled}}\n</$list>\n<$list filter='[<tv-config-toolbar-text>prefix[yes]]'>\n<span class='tc-btn-text'>\n<$text text={{$:/language/Buttons/Favorites/Caption}}/>\n</span>\n</$list>\n</$button></$list>",
            "title": "$:/plugins/ajh/favorites/template",
            "tags": "$:/tags/ViewToolbar",
            "list-after": "$:/core/ui/Buttons/more-tiddler-actions",
            "description": "{{$:/language/Buttons/Favorites/Hint}}",
            "caption": "{{ $:/core/images/star-filled}} {{$:/language/Buttons/Favorites/Caption}}"
        },
        "$:/plugins/ajh/favorites/stylesheet": {
            "text": ".tc-btn-dropdown + .tc-favorites-hidden {\n    display: none;\n}",
            "title": "$:/plugins/ajh/favorites/stylesheet",
            "tags": "$:/tags/Stylesheet"
        },
        "$:/plugins/ajh/favorites/image": {
            "text": "\\define my-svg()\n<svg class='tc-image-favorites tc-image-button' width='22pt' height='22pt' viewBox='0 0 128 128'><path fill='#ff0' stroke='#000' stroke-width='5' d='m$(m)$$(path)$'/></svg>\n\\end\n\n<$vars m='64,96.1' path={{!!path}}>\n<<my-svg>>\n</$vars>",
            "title": "$:/plugins/ajh/favorites/image",
            "tags": "$:/tags/Image",
            "path": "l32.7,24.2c2.4,1.7 5.6,-0.6 4.6,-3.4l-12.4,-38.6l32.5,-23.8c2.4,-1.7 1.1,-5.5 -1.8,-5.5l-40.2,0l-12.5,-38.5c-0.9,-2.8 -4.8,-2.8 -5.8,0l-12.5,38.5l-40.2,0c-2.9,0 -4.2,3.8 -1.8,5.5l32.5,23.8l-12.4,38.6c-0.9,2.8 2.3,5.1 4.6,3.4l32.7,-24.2z"
        }
    }
}"
        }
    }
}
