{
    "tiddlers": {
        "$:/language/locations/caption": {
            "text": "locations",
            "title": "$:/language/locations/caption"
        },
        "$:/language/locations/hint": {
            "text": "Navigate to locations",
            "title": "$:/language/locations/hint"
        },
        "$:/plugins/ajh/locations/icon": {
            "text": "\\define my-svg()\n<svg class='tc-image-locations tc-image-button' width='22pt' height='22pt' viewBox='0 0 192 192'><path fill='#090' stroke='#000' stroke-width='7.5' d='m13.5,52.2l82.5,-48l82.5,48v87.3l-82.5,48l-82.5,-48v-87.3z'/><g fill='#fff' stroke='#000' stroke-width='2'><path d='m$(m)$$(path)$'/></g></svg>\n\\end\n\n<$vars m='96,34' path={{$:/plugins/ajh/locations/image!!path}}>\n<<my-svg>>\n</$vars>",
            "title": "$:/plugins/ajh/locations/icon"
        },
        "$:/plugins/ajh/locations/readme": {
            "text": "This plugin adds the <span class='tc-btn-dropdown'>{{$:/plugins/ajh/locations/image}}</span>locations button to the Page Toolbar. The button will navigate to the Locations tiddler.\n\nSimply drag $:/plugins/ajh/locations to the top of your tiddlywiki display, save and reload.\n\nThis plugin comprises:\n\n* Icon\n* Template\n* Caption and hint",
            "title": "$:/plugins/ajh/locations/readme"
        },
        "$:/plugins/ajh/locations/image": {
            "text": "\\define my-svg()\n<svg class='tc-image-locations tc-image-button' width='22pt' height='22pt' viewBox='0 0 128 128'><path d='m$(m)$$(path)$'/></svg>\n\\end\n\n<$vars m='64,2' path={{!!path}}>\n<<my-svg>>\n</$vars>",
            "title": "$:/plugins/ajh/locations/image",
            "tags": "$:/tags/Image",
            "path": "c-24,0 -44,20 -44,44c0,33 44,82 44,82s44,-49 44,-82c0,-24 -20,-44 -44,-44zm0,24a20,20 0 0 1 0,40a20,20 0 0 1 0,-40z"
        },
        "$:/plugins/ajh/locations/template": {
            "text": "<$button tooltip={{$:/language/locations/hint}} aria-label={{$:/language/locations/caption}} class=<<tv-config-toolbar-class>>>\n<$action-navigate $to='Locations'/>\n<$list filter='[<tv-config-toolbar-icons>prefix[yes]]'>\n{{$:/plugins/ajh/locations/image}}\n</$list>\n<$list filter='[<tv-config-toolbar-text>prefix[yes]]'>\n<span class='tc-btn-text'><$text text={{$:/language/locations/caption}}/></span>\n</$list>\n</$button>",
            "title": "$:/plugins/ajh/locations/template",
            "tags": "$:/tags/PageControls",
            "description": "{{$:/language/locations/hint}}",
            "caption": "{{$:/plugins/ajh/locations/image}} {{$:/language/locations/caption}}"
        }
    }
}
